import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import DeleteIcon from '@mui/icons-material/Delete';
import DoneIcon from '@mui/icons-material/Done';
import './App.css';
import { useEffect, useState } from 'react';
import { format } from 'date-fns';



const API="http://localhost:6007/"

function App() {
            const [todos,setTodos]=useState([]);
            const [newTodo,setNewTodo]=useState("");
            const [newDate,setNewDate]=useState("");
            const [date,setDate]=useState(format(new Date(),"yyyy-mm-dd"));
            const [status,setStatus]=useState("all")
            const [filtered,setFiltered]=useState([]);


            useEffect(()=>{

              GetTodos();
              filterHandler();
              // eslint-disable-next-line react-hooks/exhaustive-deps
            },[todos]);

            const GetTodos= async()=>{
              try {
               const response= await fetch(API+"task")
               const data=await response.json();
               setTodos(data)
            }
             catch (error) {
                console.log(error)
              }
            }



            const completeTodo = async id => {
              const data = await fetch(API+"task/"+ id).then(res => res.json());

              setTodos(todos => todos.map(todo => {
                if (todo.id === data.id) {
                  todo.done = data.done;
                }

                return todo;
              }));
              GetTodos();

            }

            const addTodo = async () => {
              const data = await fetch(API+"task", {
                method: "POST",
                headers: {
                  "Content-Type": "application/json" 
                },
                body: JSON.stringify({
                  task: newTodo,
                  date: newDate,
                })
              }).then(res => res.json());

              setTodos([...todos, data]);
              setDate(newDate);

              setNewTodo("");
              setNewDate("");
            }

            const deleteTodo = async id => {
              const data = await fetch(API + 'task/' + id, { method: "DELETE" }).then(res => res.json())

              setTodos(data);
              GetTodos();

            }


            const statusHandler=(e)=>{
                setStatus(e.target.value)
            }

            const filterHandler=()=>{
              switch (status) {
                case "completed":
                  setFiltered(todos.filter((todo)=>todo.done===true))
                  break;
                case "uncompleted":
                  setFiltered(todos.filter((todo)=>todo.done===false))
                  break;
                default:
                  setFiltered(todos);
                  break;

              }
            }



  return (
    <div className="App">
      <div className='container'>
         <h1>ToDo App</h1>

         <div className='add-container'>
         <TextField
        hiddenLabel
        id="filled-hidden-label-normal"
        placeholder='Enter the todo task'
        variant="standard"
        className='text-field'
        onChange={e=>setNewTodo(e.target.value)}
        value={newTodo}
      />
      <input type="date" name="date" className='text-field' onChange={e=>setNewDate(e.target.value)}
        value={newDate}/>
       <Button
       type ="submit"
        variant="contained"
        sx={{"&:hover":{bgcolor:"hotpink"},bgcolor:"deeppink"}} 
        onClick={addTodo} >   
        ADD
      </Button>
      <div className='select-task'>
        <select onChange={statusHandler} className='todos-filter'>
          <option value="all">All</option>
          <option value="completed">Completed</option>
          <option value="uncompleted">Uncompleted</option>
        </select>
      </div>
      </div>
      <div className='tasks'>
        <h3>Your Tasks</h3>
          {filtered.length>0 ? filtered.map(todo=>(
					<div className={
						"todo" + (todo.done ? " is-complete" : "")
					} key={todo._id} >
						<div className="text">{todo.task}</div>
            <div className={'date' +(date!==new Date().toISOString().slice(0, 10) ? "deadline":"")} >
              {todo.date}
            </div>
            <div className='done-icon' onClick={() => completeTodo(todo._id)} >
              <DoneIcon/>
            </div>
						<div className="delete-icon" onClick={() => deleteTodo(todo._id)}><DeleteIcon/></div>
					</div>
				)) : (
					<p>You currently have no tasks</p>)}

      </div>


      </div>
    </div>
  );
}

export default App;
