import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import './App.css';
import { useEffect, useState } from 'react';

const API="http://localhost:6007"

function App() {
            const [todos,setTodos]=useState([]);
            const [newTodo,setNewTodo]=useState("");

            const GetTodos=()=>{
              fetch(API +"/task")
              .then(res=>res.json())
              .then(data=>setTodos(data))
              .catch((err)=>console.log(err))
            }

            useEffect(()=>{
              GetTodos()
            },[]);

            const completeTodo = async id => {
              const data = await fetch(API + '/task/' + id).then(res => res.json());

              setTodos(todos => todos.map(todo => {
                if (todo._id === data._id) {
                  todo.complete = data.complete;
                }

                return todo;
              }));

            }

            const addTodo = async () => {
              const data = await fetch(API + "/task", {
                method: "POST",
                headers: {
                  "Content-Type": "application/json" 
                },
                body: JSON.stringify({
                  text: newTodo
                })
              }).then(res => res.json());

              setTodos([...todos, data]);

              setNewTodo("");
            }

            const deleteTodo = async id => {
              const data = await fetch(API + '/task/' + id, { method: "DELETE" }).then(res => res.json());

              setTodos(todos => todos.filter(todo => todo._id !== data.result._id));
            }

  return (
    <div className="App">
      <div className='container'>
         <h1>ToDo App</h1>

         <div>
         <TextField
        hiddenLabel
        id="filled-hidden-label-normal"
        placeholder='Enter the todo task'
        variant="standard"
        onChange={e=>setNewTodo(e.target.value)}
        value={newTodo}
      />
       <Button
       type ="submit"
        variant="contained"
        sx={{"&:hover":{bgcolor:"hotpink"},bgcolor:"deeppink"}} 
        onClick={addTodo} >   
        ADD
      </Button>

      <div className='tasks'>
        <h4>Your Tasks</h4>
          {todos.length>0 ? todos.map(todo=>(
					<div className={
						"todo" + (todo.complete ? " is-complete" : "")
					} key={todo._id} onClick={() => completeTodo(todo._id)}>
						<div className="checkbox"></div>

						<div className="text">{todo.text}</div>

						<div className="delete-todo" onClick={() => deleteTodo(todo._id)}>x</div>
					</div>
				)) : (
					<p>You currently have no tasks</p>)}

      </div>

         </div>
      </div>
    </div>
  );
}

export default App;
