import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import DeleteIcon from '@mui/icons-material/Delete';
import DoneIcon from '@mui/icons-material/Done';
import './App.css';
import { useEffect, useState } from 'react';


const API="http://localhost:6007"

function App() {
            const [todos,setTodos]=useState([]);
            const [newTodo,setNewTodo]=useState("");
            const [status,setStatus]=useState("all")
            const [filtered,setFiltered]=useState([]);

            useEffect(()=>{

              GetTodos();
            },[]);


            const GetTodos= async()=>{
              try {
               const response= await fetch("http://localhost:6007/task")
               const data=await response.json();
               setTodos(data)
            }
             catch (error) {
                console.log(error)
              }
            }

            const completeTodo = async id => {
              const data = await fetch(API+"/task/"+ id).then(res => res.json());

              setTodos(todos => todos.map(todo => {
                if (todo.id === data.id) {
                  todo.done = data.done;
                }

                return todo;
              }));
              GetTodos();

            }

            const addTodo = async () => {
              const data = await fetch(API+"/task", {
                method: "POST",
                headers: {
                  "Content-Type": "application/json" 
                },
                body: JSON.stringify({
                  task: newTodo
                })
              }).then(res => res.json());

              setTodos([...todos, data]);

              setNewTodo("");
            }

            const deleteTodo = async id => {
              const data = await fetch(API + '/task/' + id, { method: "DELETE" }).then(res => res.json())

              setTodos(data);


            }
            GetTodos();

            const statusHandler=(e)=>{
                setStatus(e.target.value)
            }

            const filterHandler=()=>{
              switch (status) {
                case "completed":
                  setFiltered(todos.filter((todo)=>todo.completed===true))
                  break;


              }
            }



  return (
    <div className="App">
      <div className='container'>
         <h1>ToDo App</h1>

         <div className='add-container'>
         <TextField
        hiddenLabel
        id="filled-hidden-label-normal"
        placeholder='Enter the todo task'
        variant="standard"
        className='text-field'
        onChange={e=>setNewTodo(e.target.value)}
        value={newTodo}
      />
       <Button
       type ="submit"
        variant="contained"
        sx={{"&:hover":{bgcolor:"hotpink"},bgcolor:"deeppink"}} 
        onClick={addTodo} >   
        ADD
      </Button>
      <div className='select-task'>
        <select onChange={statusHandler} className='todos-filter'>
          <option value="all">All</option>
          <option value="completed">Completed</option>
          <option value="uncompleted">Uncompleted</option>
        </select>
      </div>
      </div>
      <div className='tasks'>
        <h3>Your Tasks</h3>
          {todos.length>0 ? todos.map(todo=>(
					<div className={
						"todo" + (todo.done ? " is-complete" : "")
					} key={todo._id} >
						<div className="text">{todo.task}</div>
            <div className='done-icon' onClick={() => completeTodo(todo._id)} >
              <DoneIcon/>
            </div>
						<div className="delete-icon" onClick={() => deleteTodo(todo._id)}><DeleteIcon/></div>
					</div>
				)) : (
					<p>You currently have no tasks</p>)}

      </div>


      </div>
    </div>
  );
}

export default App;
